class Plugin(BasePlugin):
    """
    Insecure Deserialization plugin
    """
    def __init__(self):
        self.name = "insecure_deserialization"
        self.description = "Detects Insecure Deserialization vulnerabilities"

    async def run(self, target: str, requester, oast_server: str = None):
        """
        Run the Insecure Deserialization plugin
        """
        headers = {"Content-Type": "application/x-java-serialized-object"}
        try:
            response = await requester.post(target, data=PAYLOAD, headers=headers)
            content = await response.text()
            if "java.io.InvalidClassException" in content or \
               "java.io.StreamCorruptedException" in content:
                return f"Potential Insecure Deserialization vulnerability found at {target}"
        except Exception:
            pass
        
        return None
